function Feed() {    

    const [posts, setPosts] = useState([]);

    let comment = {key : 'hello',
    displayName : 'Allsee Technologies',
    username : 'Allsee Tech',
    verified :  true,
    text :'hello',
    avatar : "https://pbs.twimg.com/profile_images/1720020150613651456/KMq5JBTI_400x400.jpg", }

    let comment2 = {key : 'cool',
    displayName : 'Allsee Technologies',
    username : 'Allsee Tech',
    verified :  true,
    text :'cool',
    avatar : "https://pbs.twimg.com/profile_images/1720020150613651456/KMq5JBTI_400x400.jpg", }

    let comment3 = {key : 'great',
    displayName : 'Allsee Technologies',
    username : 'Allsee Tech',
    verified :  true,
    text :'',
    avatar : "https://pbs.twimg.com/profile_images/1720020150613651456/KMq5JBTI_400x400.jpg", }

    useEffect(() => {
        setPosts([...posts, comment, comment2, comment3]);
    }, [])

    console.log(posts)

    return (
        <div className = "feed">
            <div className = "feed__header">
                <h2>Home</h2>
            </div>

            <TweetBox />
            <FlipMove>
                {posts.map(post => (
                <Post 
                key = {post.text}
                displayName = {post.displayName}
                username = {post.username}
                verified = {post.verified}
                text = {post.text}
                image = {post.image}
                avatar = {post.avatar}
            />
            ))}
            </FlipMove>   
        </div>
    )
}
export default Feed
